css:
.invoice-container {
  font-family: Arial, sans-serif;
  font-size: 12px;
  padding: 20px;
}
.text-center {
  text-align: center;
  font-weight: bold;
}
.text-right {
  text-align: right;
}
.full-table {
  width: 100%;
  border-collapse: collapse;
  margin-top: 10px;
  margin-bottom: 10px;
}
.full-table td, .full-table th {
  padding: 6px;
  vertical-align: top;
}
.border th, .border td {
  border: 1px solid #000;
}
hr {
  border: 0;
  border-top: 1px solid #000;
  margin: 10px 0;
}
